import React from 'react';
import '../App.css'; // Import the CSS file

const CartSummary = ({ cart, removeFromCart }) => {
  // Calculate total price and quantity
  const totalPrice = cart.reduce((total, product) => total + product.price * product.quantity, 0);
  const totalQuantity = cart.reduce((total, product) => total + product.quantity, 0);

  return (
    <div className="cartSummary">
      <h2 className="headerCart">Cart Summary</h2>
      <ul className="list-group">
        {cart.map(item => (
          <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center">
            <div>
              <span>{item.name}</span>
              <span className="badge badge-primary badge-pill ml-2">₱{item.price.toFixed(2)}</span>
              <span className="badge badge-secondary badge-pill ml-2">{item.quantity}</span>
            </div>
            <button className="btn btn-danger btn-sm" onClick={() => removeFromCart(item.id)}>Remove</button>
          </li>
        ))}
      </ul>
      <p>Total Price: ₱{totalPrice.toFixed(2)}</p>
      <p>Total Quantity: {totalQuantity}</p>
    </div>
  );
};
<CartSummary cart={cart} removeFromCart={removeFromCart} />

export default CartSummary;
