import React from 'react';

const ViewCart = ({ cart, removeFromCart }) => {
  const totalPrice = cart.reduce((total, product) => total + product.price * product.quantity, 0);
  const totalQuantity = cart.reduce((total, product) => total + product.quantity, 0);
  
  
  return (
    
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <h2 className="card-header">My Cart</h2>
            <ul className="list-group list-group-flush">
              {cart.map(item => (
                <li key={item.id} className="list-group-item d-flex flex-column align-items-start"> {/* Changed flex direction to column */}
                  <div className="d-flex justify-content-between align-items-center w-100">
                    <span>{item.name}</span>
                    <button className="btn btn-danger" onClick={() => removeFromCart(item.id)}>Remove</button>
                  </div>
                  <span className="badge bg-primary rounded-pill mt-2">Quantity: {item.quantity}</span> {/* Moved quantity badge below item name */}
                  <span className="badge bg-secondary mt-2">Price: ₱{item.price.toFixed(2)}</span> {/* Moved price badge below item name */}
                </li>
              ))}
            </ul>
            <div className="card-footer">
              <p className="card-text">Total Price: ₱{totalPrice.toFixed(2)}</p>
              <p className="card-text">Total Quantity: {totalQuantity}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
    
  );
};

export default ViewCart;
