import React from 'react';

const ViewCart = ({ cart, removeFromCart }) => {
  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh' }}>
      <div style={{ width: '60%' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>View Cart</h2>
        <ul className="list-group" style={{ padding: '20px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
          {cart.map(item => (
            <li key={item.id} className="list-group-item" style={{ marginBottom: '10px', borderRadius: '8px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span>{item.name}</span>
                <span className="badge bg-primary rounded-pill">Quantity: {item.quantity}</span>
                <span className="badge bg-secondary">Price: {item.price}</span>
                <button className="btn btn-danger" onClick={() => removeFromCart(item.id)}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ViewCart;
