import React from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import '../App.css'; // Import the CSS file

const Product = ({ id, name, description, price, addToCart }) => {
  return (
    <div className="card">
      <div className="card-body">
        <h2 className="card-title">{name}</h2>
        <p className="card-text">{description}</p>
        <p className="card-text">â‚±{price.toFixed(2)}</p>
        <button className="btn btn-primary" onClick={() => addToCart(id)}>Add to Cart</button>
      </div>
    </div>
  );
};

<Routes>
            <Route path="/" element={<Home />} />
            <Route path="/products" element={<ProductPages products={products} addToCart={addToCart} />} />
            <Route path="/cart" element={<ViewCart cart={cart} removeFromCart={removeFromCart} />} />
          </Routes>
function ViewCart() {
  return (
    <div>
      <h2>Home</h2>
      <button>
        <Link to="/cart" style={{ textDecoration: 'none', color: 'inherit' }}>My Cart</Link>
      </button>
    </div>
  );
}

export { Product};
