import React from 'react';

const ViewCart = ({ cart, removeFromCart }) => {
  const totalPrice = cart.reduce((total, product) => total + product.price * product.quantity, 0);
  const totalQuantity = cart.reduce((total, product) => total + product.quantity, 0);
  
  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <h2 className="card-header">My Cart</h2>
            <ul className="list-group list-group-flush">
              {cart.map(item => (
                <li key={item.id} className="list-group-item position-relative"> {/* Added position-relative class */}
                  <span className="d-inline-block mw-50">{item.name}</span> {/* Ensure the item name doesn't overflow */}
                  <span className="badge bg-primary rounded-pill position-absolute top-50 start-100 translate-middle">Quantity: {item.quantity}</span> {/* Positioned quantity badge */}
                  <span className="badge bg-secondary position-absolute top-50 start-100 translate-middle">Price: ₱{item.price.toFixed(2)}</span> {/* Positioned price badge */}
                  <button className="btn btn-danger position-absolute top-0 end-0 translate-middle" onClick={() => removeFromCart(item.id)}>Remove</button> {/* Positioned remove button */}
                </li>
              ))}
            </ul>
            <div className="card-footer">
              <p className="card-text">Total Price: ₱{totalPrice.toFixed(2)}</p>
              <p className="card-text">Total Quantity: {totalQuantity}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewCart;
