import React from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import '../App.css'; // Import the CSS file

const Product = ({ id, name, description, price, addToCart }) => {
  return (
    <div>
      <div className="card">
        <div className="card-body">
          <h2 className="card-title">{name}</h2>
          <p className="card-text">{description}</p>
          <p className="card-text">â‚±{price.toFixed(2)}</p>
          <button className="btn btn-primary" onClick={() => addToCart(id)}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
};



// Add the "My Cart" button outside the product cards
const ProductPages = ({ products, addToCart }) => {
  return (
    <div>
      <div className="container">
        <div className="row"> 
          {products.map(product => (
            <div className="col-md-4" key={product.id}>
              <Product
                id={product.id}
                name={product.name}
                description={product.description}
                price={product.price}
                addToCart={addToCart}
              />
            </div>
          ))}
        </div>
      </div>
      {/* Add the "My Cart" button outside the product cards */}
      <div style={{ marginTop: '20px', textAlign: 'center' }}>
        <Link to="/cart">
          <button className="btn btn-primary">My Cart</button>
        </Link>
      </div>
    </div>
  );
};

export default ProductPages;
