import React from 'react';
import '../App.css'; // Import the CSS file

const ViewCart = ({ cart }) => {
  // Calculate total price and quantity
  const totalPrice = cart.reduce((total, product) => total + product.price * product.quantity, 0);
  const totalQuantity = cart.reduce((total, product) => total + product.quantity, 0);

  return (
    <div className="viewCart text-center mt-4"> {/* Centered and added margin-top */}
      <h2 className="headerCart mb-4">Cart Summary</h2> {/* Added margin-bottom */}
      <ul className="list-group"> {/* Changed table to ul and added list-group class */}
        {cart.map(product => (
          <li key={product.id} className="list-group-item d-flex justify-content-between align-items-center">
            <span>{product.name}</span>
            <span>₱{product.price.toFixed(2)}</span>
            <span>{product.quantity}</span>
          </li>
        ))}
      </ul>
      <div className="mt-4"> {/* Added margin-top */}
        <p>Total Price: ₱{totalPrice.toFixed(2)}</p>
        <p>Total Quantity: {totalQuantity}</p>
      </div>
    </div>
  );
};

export default ViewCart;
